#include <math.h>

double atan( double x );

float atanf( float x );

long double atanl( long double x );

#include <stdio.h>
#include <math.h>
#include <stdlib.h>
#include <fenv.h>

int main( void )
{
    int except_flags;

    feclearexcept(FE_ALL_EXCEPT);

    printf( "%f\n", atan(.5) );

    except_flags = fetestexcept(FE_ALL_EXCEPT);
    if(except_flags) {
        /* An error occurred; handle it appropriately. */
    }

    return EXIT_SUCCESS;
}

